mapper.select:
SELECT count(*) FROM member


		WHERE member_email = #{memberEmail}


		WHERE member_nick_name = #{memberNickName}

mapper.update:
UPDATE member


		member_pw = #{memberPw}


		member_zipcode = #{memberZipcode}


		member_address = #{memberAddress}


		member_daddress = #{memberDaddress}


		member_tel = #{memberTel}


WHERE member_email = #{memberEmail}
